import { Checkbox } from 'antd';
import React from 'react';
import axios from 'axios'

export default class CreatePoll extends React.Component{

    onChange = (checkedValues) => {
        console.log('checked = ', checkedValues);
    }
    topicId = this.props.location.state
    signIn = async () => {
        try {
            const urlOption = `http://localhost:3020/option/`+this.topicId

            const urlTopic = `http://localhost:3020/topic/`+this.topicId
            const responseOption = await axios.get(urlOption)
            const responseTopic = await axios.get(urlTopic)
            (responseTopic.data.content, responseOption.data[0].content);
            const options = [
                // { label: a, value: a },
                { label: 'Pear', value: 'Pear' },
                { label: 'Orange', value: 'Orange' },
            ];
            return options
            // return();

        } catch (err) {
        console.log(err);
        }
    };


    render(){
        console.log(this.signIn);

        return (
            <>
                <br />
                <br />
                <Checkbox.Group options={this.signIn} className='option' onChange={this.signIn} />
                <br />
                <br />
            </>
        )
    }
}
