import { Checkbox } from 'antd';
import React from 'react';
import axios from 'axios'

export default class CreatePoll extends React.Component{

    onChange = (checkedValues) => {
        console.log('checked = ', checkedValues);
    }


    render(){

        const plainOptions = ['Apple', 'Pear', 'Orange'];
        var topicId = this.props.location.state
        const getPoll = async (topicId)=> {
            try {
                const response = await axios.get("http://localhost:3020/poll", topicId );
                if(response){

                    return (
                        <>
                            <Checkbox.Group options={plainOptions}  onChange={this.onChange} onLoad = {getPoll} />
                            <br />
                            <br />
                            <Checkbox.Group options={options} onChange={this.onChange} />
                            <br />
                            <br />
                        </>
                    )
                }
                else{

                }
            } catch (err) {
            console.log(err);
            }
        }
        const options = [
            { label: 'Apple', value: 'Apple' },
            { label: 'Pear', value: 'Pear' },
            { label: 'Orange', value: 'Orange' },
        ];
        return (
            <>
                <Checkbox.Group options={plainOptions}  onChange={this.onChange} onLoad = {this.getPoll(topicId)} />
                <br />
                <br />
                <Checkbox.Group options={options} onChange={this.onChange} />
                <br />
                <br />
            </>
        )
    }
}
